import React from "react";
import { Route, Switch } from "react-router-dom";

import MainPage from "pages/MainPage/MainPage.js";
import NewArticle from "pages/NewArticle/NewArticle.js";
import Authentication from "pages/Authentication/Authentication.js";
import UserPage from "pages/UserPage/UserPage.js";
import Article from "pages/Article/Article.js";

export default ({ currentLang, isLogin, setIsLogin }) => (
  <Switch>
    <Route path="/medium-clone/" component={() => <MainPage currentLang={currentLang} />} exact />
    <Route path="/medium-clone/login" component={() => <Authentication currentLang={currentLang} setIsLogin={setIsLogin} isLogin={isLogin} />} />
    <Route path="/medium-clone/register" component={() => <Authentication currentLang={currentLang} setIsLogin={setIsLogin} isLogin={isLogin} />} />
    <Route path="/medium-clone/new" component={() => <NewArticle currentLang={currentLang} />} />
    <Route path="/medium-clone/profiles/:id" component={() => <UserPage setIsLogin={setIsLogin} isLogin={isLogin} currentLang={currentLang} />} />
    <Route path="/medium-clone/articles/:articleId" component={() => <Article currentLang={currentLang} />} exact />
    <Route path="/medium-clone/articles/:articleId/edit" component={() => <NewArticle currentLang={currentLang} />} />
  </Switch>
)

